	WHERE   1 = 1

      AND Activity_ID = #{ID,jdbcType=BIGINT}


      AND Activity_AgentID = #{UID,jdbcType=BIGINT}
       AND UNIX_TIMESTAMP(NOW()) <= UNIX_TIMESTAMP(Activity_EndTime) 


      And Activity_Type = #{Type}




    		And Activity_AreaCode = #{code}



       		And Activity_AreaCode LIKE #{code}




       		And Activity_AreaCode NOT LIKE #{code}


    	 AND UNIX_TIMESTAMP(NOW()) <= UNIX_TIMESTAMP(Activity_EndTime) 


      AND #{Rand} = #{Rand}

    AND Activity_Delete = 0
  ORDER BY Activity_ModifyTime ASC

      LIMIT #{start},#{size}

