mapper.sql:
WHERE
1 = 1

	AND Dict_type = #{type}


	AND Dict_number = #{number}


	AND Dict_value = #{value}


	AND Dict_status = #{Status}


	AND #{Rand} = #{Rand}

mapper.update:
update rb_dict


    Dict_value = #{dictValue,jdbcType=VARCHAR},


    Dict_number = #{dictNumber,jdbcType=VARCHAR},


    Dict_delete = #{dictDelete,jdbcType=INTEGER},


    Dict_status = #{dictStatus,jdbcType=INTEGER},

  Dict_modifytime = now()

where 1=1

    AND Dict_value = #{dictValue}


    AND Dict_value = #{dictValue}


    AND Dict_type = #{dictType}


    AND Dict_number = #{dictNumber}
