mapper.sql:
WHERE
	   1 = 1

    AND PushRecord_ID = #{ID}


    AND PushRecord_PushUserID = #{UID}



     AND PushRecord_Title LIKE CONCAT(CONCAT('%', #{KeyWord}), '%')



    AND PushRecord_Status = #{Type}


    AND #{Rand} = #{Rand}

    AND PushRecord_Deleted = 0

mapper.insert:
insert into rb_pushrecords (PushRecord_ID, PushRecord_UserID, PushRecord_PushID, 
  PushRecord_TypeID, PushRecord_Title, PushRecord_Content, 
  PushRecord_CreateTime, PushRecord_ModifyTime, 
  PushRecord_Version, PushRecord_Status, PushRecord_Deleted
  )
values (#{pushrecordId,jdbcType=BIGINT}, #{pushrecordUserid,jdbcType=INTEGER}, #{pushrecordPushid,jdbcType=BIGINT}, 
  #{pushrecordTypeid,jdbcType=BIGINT}, #{pushrecordTitle,jdbcType=VARCHAR}, #{pushrecordContent,jdbcType=VARCHAR}, 
  #{pushrecordCreatetime,jdbcType=TIMESTAMP}, #{pushrecordModifytime,jdbcType=TIMESTAMP}, 
  #{pushrecordVersion,jdbcType=INTEGER}, #{pushrecordStatus,jdbcType=TINYINT}, #{pushrecordDeleted,jdbcType=TINYINT}
  )
