mapper.select:
SELECT rs_number, rs_title, rs_reason, rs_publish, rs_author, rs_starttime, rs_endtime, rs_status, rs_grade, rs_modifytime, (SELECT User_Name FROM rb_user WHERE User_Id = rs.rs_belongid) AS user_name, (SELECT User_HeadIcon FROM rb_user WHERE User_Id = rs.rs_belongid) AS user_headicon FROM rb_saveread AS rs
AND rs_type > 0 ORDER BY rs_modifytime DESC
LIMIT #{start},#{size}

SELECT rs_number, rs_title, rs_reason, rs_publish, rs_author, rs_starttime, rs_endtime, rs_status, rs_grade, rs_type, rs_modifytime, (SELECT User_Name FROM rb_user WHERE User_Id = rs.rs_belongid) AS user_name, (SELECT User_HeadIcon FROM rb_user WHERE User_Id = rs.rs_belongid) AS user_headicon FROM rb_saveread AS rs, rb_userinfo AS ru
AND userinfo_userid = rs_belongid AND rs_type > 0 ORDER BY rs_modifytime DESC
LIMIT #{start},#{size}

