mapper.sql:
WHERE
1 = 1

	AND Video_Id = #{ID}


	AND Video_UserId = #{UID}


	AND #{Rand} = #{Rand}

mapper.update:
UPDATE rb_video 
SET Video_CommentCount = Video_CommentCount + 1
WHERE Video_Id = #{videoId }

mapper.select:
    select 
	 Video_UserId
	 from rb_video where Video_Id=#{videoId}

    select 
	 Video_UseMoney
	 from rb_video where Video_Id=#{videoId}
